import React, { useState, useEffect, useReducer, useRef, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { GoogleOAuthProvider } from '@react-oauth/google';
import { AuthProvider, useAuth } from './components/AuthContext';
import { uploadFileWithProgress, createTimerWithPersistence } from './components/asyncOperations';
import './App.css';
import { checkOllamaStatus } from './components/ollamaService';
// –ò–º–ø–æ—Ä—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤
import Navigation from './components/Navigation';
import HomePage from './components/HomePage';
import ExhibitCatalog from './components/ExhibitCatalog';
import ExhibitDetail from './components/ExhibitDetail';
import ExhibitForm from './components/ExhibitForm';
import ExhibitScanner from './components/ExhibitScanner';
import TourPlanner from './components/TourPlanner';
import ConservationTracker from './components/ConservationTracker';
import ExhibitAnalytics from './components/ExhibitAnalytics';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import TimeDisplay from './components/TimeDisplay';

// Reducer –¥–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏–µ–º –º—É–∑–µ—è
const museumReducer = (state, action) => {
  switch (action.type) {
    case 'SET_EXHIBITS':
      return { ...state, exhibits: action.payload };
    case 'ADD_EXHIBIT':
      return { ...state, exhibits: [...state.exhibits, action.payload] };
    case 'UPDATE_EXHIBIT':
      return {
        ...state,
        exhibits: state.exhibits.map(e => 
          e._id === action.payload._id ? action.payload : e
        )
      };
    case 'DELETE_EXHIBIT':
      return {
        ...state,
        exhibits: state.exhibits.filter(e => e._id !== action.payload)
      };
    case 'SET_FILTER':
      return { ...state, filter: action.payload };
    case 'SET_SEARCH':
      return { ...state, searchQuery: action.payload };
    case 'SET_SORT':
      return { ...state, sortBy: action.payload.field, sortOrder: action.payload.order };
    default:
      return state;
  }
};

function MainApp() {
  const [ollamaStatus, setOllamaStatus] = useState(null);

  useEffect(() => {
    const checkOllama = async () => {
      const status = await checkOllamaStatus();
      setOllamaStatus(status);
    };
    checkOllama();
  }, []);

  const { user, notifications, addNotification } = useAuth();
  const [museumState, dispatch] = useReducer(museumReducer, {
    exhibits: [],
    filter: 'all',
    searchQuery: '',
    sortBy: 'createdAt',
    sortOrder: 'desc'
  });

  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef(null);
  const searchInputRef = useRef(null);

  // 1. onClick - –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–ª–∏–∫–∞ –ø–æ —ç–∫—Å–ø–æ–Ω–∞—Ç—É
  const handleExhibitClick = useCallback((exhibit) => {
    console.log('–≠–∫—Å–ø–æ–Ω–∞—Ç –∫–ª–∏–∫–Ω—É—Ç:', exhibit.name);
    addNotification(`–û—Ç–∫—Ä—ã—Ç —ç–∫—Å–ø–æ–Ω–∞—Ç: ${exhibit.name}`, 'info');
  }, [addNotification]);

  // 2. onChange - –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è –ø–æ–∏—Å–∫–∞
  const handleSearchChange = (e) => {
    dispatch({ type: 'SET_SEARCH', payload: e.target.value });
    console.log('–ü–æ–∏—Å–∫ –∏–∑–º–µ–Ω–µ–Ω:', e.target.value);
  };

  // 3. onSubmit - –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º—ã –ø–æ–∏—Å–∫–∞
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    console.log('–§–æ—Ä–º–∞ –ø–æ–∏—Å–∫–∞ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∞:', museumState.searchQuery);
    addNotification(`–ü–æ–∏—Å–∫: ${museumState.searchQuery}`, 'info');
  };

  // 4. onMouseEnter - –ü—Ä–∏ –Ω–∞–≤–µ–¥–µ–Ω–∏–∏ –Ω–∞ –∫–∞—Ä—Ç–æ—á–∫—É
  const handleMouseEnter = (exhibit) => {
    console.log('–ù–∞–≤–µ–¥–µ–Ω–æ –Ω–∞ —ç–∫—Å–ø–æ–Ω–∞—Ç:', exhibit.name);
  };

  // 5. onMouseLeave - –ü—Ä–∏ —É—Ö–æ–¥–µ —Å –∫–∞—Ä—Ç–æ—á–∫–∏
  const handleMouseLeave = (exhibit) => {
    console.log('–£—à–ª–∏ —Å —ç–∫—Å–ø–æ–Ω–∞—Ç–∞:', exhibit.name);
  };

  // 6. onKeyDown - –û–±—Ä–∞–±–æ—Ç–∫–∞ –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã
  const handleKeyDown = (e) => {
    if (e.key === 'Escape' && searchInputRef.current) {
      searchInputRef.current.blur();
      dispatch({ type: 'SET_SEARCH', payload: '' });
    }

    if (e.ctrlKey && e.key === 'f') {
      e.preventDefault();
      if (searchInputRef.current) {
        searchInputRef.current.focus();
      }
    }
  };

  // 7. onFocus - –ü—Ä–∏ —Ñ–æ–∫—É—Å–µ –Ω–∞ –ø–æ–ª–µ
  const handleFocus = (fieldName) => {
    console.log('–§–æ–∫—É—Å –Ω–∞ –ø–æ–ª–µ:', fieldName);
  };

  // 8. onBlur - –ü—Ä–∏ –ø–æ—Ç–µ—Ä–µ —Ñ–æ–∫—É—Å–∞
  const handleBlur = (fieldName) => {
    console.log('–ü–æ—Ç–µ—Ä—è —Ñ–æ–∫—É—Å–∞:', fieldName);
  };

  // 9. onDoubleClick - –î–≤–æ–π–Ω–æ–π –∫–ª–∏–∫
  const handleDoubleClick = (exhibit) => {
    console.log('–î–≤–æ–π–Ω–æ–π –∫–ª–∏–∫ –ø–æ —ç–∫—Å–ø–æ–Ω–∞—Ç—É:', exhibit.name);
    addNotification(`–ë—ã—Å—Ç—Ä—ã–π –ø—Ä–æ—Å–º–æ—Ç—Ä: ${exhibit.name}`, 'info');
  };

  // 10. onContextMenu - –ö–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–µ –º–µ–Ω—é
  const handleContextMenu = (e, exhibit) => {
    e.preventDefault();
    console.log('–ö–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–µ –º–µ–Ω—é –¥–ª—è:', exhibit.name);
  };
// –ó–ê–ú–ï–ù–ò–¢–ï —Ñ—É–Ω–∫—Ü–∏—é loadExhibits –≤ useEffect:
const loadExhibits = async () => {
  try {
    console.log('üîÑ –ó–∞–≥—Ä—É–∑–∫–∞ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤...');

    // –ü—Ä–æ–±—É–µ–º –ø–æ–ª—É—á–∏—Ç—å —Ç–æ–∫–µ–Ω –∏–∑ localStorage
    const token = localStorage.getItem('authToken') || 'demo-token-visitor';

    const response = await fetch('http://localhost:5000/api/bits', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      }
    });

    console.log('–°—Ç–∞—Ç—É—Å –æ—Ç–≤–µ—Ç–∞:', response.status);

    if (response.ok) {
      const data = await response.json();
      console.log('üì¶ –ü–æ–ª—É—á–µ–Ω—ã —ç–∫—Å–ø–æ–Ω–∞—Ç—ã:', data.bits?.length || 0);

      if (data.success) {
        dispatch({ type: 'SET_EXHIBITS', payload: data.bits || [] });
        addNotification(`–ó–∞–≥—Ä—É–∂–µ–Ω–æ ${data.bits?.length || 0} —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤`, 'success');
      } else {
        console.error('–û—à–∏–±–∫–∞ –≤ –¥–∞–Ω–Ω—ã—Ö:', data.message);
      }
    } else if (response.status === 401) {
      console.log('‚ö†Ô∏è –ù–µ –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω, –ø—Ä–æ–±—É–µ–º –±–µ–∑ —Ç–æ–∫–µ–Ω–∞...');

      // –ü—Ä–æ–±—É–µ–º –±–µ–∑ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏
      const publicResponse = await fetch('http://localhost:5000/api/bits');
      if (publicResponse.ok) {
        const publicData = await publicResponse.json();
        dispatch({ type: 'SET_EXHIBITS', payload: publicData.bits || [] });
      }
    } else {
      console.error('‚ùå –û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞:', response.status);
      addNotification('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —ç–∫—Å–ø–æ–Ω–∞—Ç—ã', 'error');
    }
  } catch (error) {
    console.error('‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤:', error);
    addNotification('–û—à–∏–±–∫–∞ —Å–µ—Ç–∏ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤', 'error');
  }
};

  // –ê—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤
  useEffect(() => {

    loadExhibits();
  }, []);

  // –ê—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ —Ñ–∞–π–ª–æ–≤
  const handleFileUpload = async (file) => {
    setIsUploading(true);
    setUploadProgress(0);

    try {
      await uploadFileWithProgress(
        file,
        (progress) => {
          setUploadProgress(progress);
          console.log(`–ü—Ä–æ–≥—Ä–µ—Å—Å –∑–∞–≥—Ä—É–∑–∫–∏: ${progress}%`);
        },
        (response) => {
          console.log('–§–∞–π–ª –∑–∞–≥—Ä—É–∂–µ–Ω:', response);
          addNotification('–§–∞–π–ª —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω', 'success');
          setIsUploading(false);
        }
      );
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', error);
      addNotification('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–∞–π–ª–∞', 'error');
      setIsUploading(false);
    }
  };

  // –ê—Å–∏–Ω—Ö—Ä–æ–Ω–Ω—ã–π —Ç–∞–π–º–µ—Ä
  useEffect(() => {
    const timer = createTimerWithPersistence(
      'sessionTimer',
      3600, // 1 —á–∞—Å
      (remaining) => {
        console.log('–¢–∞–π–º–µ—Ä —Å–µ—Å—Å–∏–∏:', remaining);
      },
      () => {
        addNotification('–°–µ—Å—Å–∏—è –∏—Å—Ç–µ–∫–ª–∞', 'warning');
      }
    );

    return () => {
      timer.stop();
    };
  }, [addNotification]);

  // –ó–∞–≥—Ä—É–∑–∫–∞ —Ñ–∞–π–ª–∞ –ø–æ –∫–ª–∏–∫—É
  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –≤—ã–±–æ—Ä–∞ —Ñ–∞–π–ª–∞
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      handleFileUpload(file);
    }
  };

  return (
    <div className="app" onKeyDown={handleKeyDown} tabIndex="0">
      <Navigation 
        user={user} 
        onSearchSubmit={handleSearchSubmit}
        searchInputRef={searchInputRef}
        onSearchChange={handleSearchChange}
        searchQuery={museumState.searchQuery}
      />

      {/* –°–∫—Ä—ã—Ç—ã–π input –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–∞–π–ª–æ–≤ */}
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />

      {/* –ü—Ä–æ–≥—Ä–µ—Å—Å –∑–∞–≥—Ä—É–∑–∫–∏ */}
      {isUploading && (
        <div className="upload-progress">
          <div className="progress-bar" style={{ width: `${uploadProgress}%` }} />
          <span>{uploadProgress}%</span>
        </div>
      )}

      {/* –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –≤—Ä–µ–º–µ–Ω–∏ */}
      <TimeDisplay />

      {/* –£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è */}
      <div className="notifications-container">
        {notifications.map(notification => (
          <div 
            key={notification.id} 
            className={`notification notification-${notification.type}`}
            onClick={() => console.log('–£–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ –∑–∞–∫—Ä—ã—Ç–æ')}
          >
            {notification.message}
          </div>
        ))}
      </div>

      <main className="main-content">
        <Routes>
          <Route path="/" element={<HomePage exhibits={museumState.exhibits} />} />
          <Route path="/exhibits" element={
            <ExhibitCatalog 
              exhibits={museumState.exhibits}
              onExhibitClick={handleExhibitClick}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
              onDoubleClick={handleDoubleClick}
              onContextMenu={handleContextMenu}
              searchQuery={museumState.searchQuery}
              onSearchChange={handleSearchChange}
              onSearchSubmit={handleSearchSubmit}
            />
          } />
          <Route path="/exhibits/:id" element={<ExhibitDetail />} />
          <Route path="/exhibits/new" element={<ProtectedRoute><ExhibitForm /></ProtectedRoute>} />
          <Route path="/exhibits/:id/edit" element={<ProtectedRoute><ExhibitForm mode="edit" /></ProtectedRoute>} />
          <Route path="/scanner" element={<ProtectedRoute><ExhibitScanner /></ProtectedRoute>} />
          <Route path="/tours" element={<ProtectedRoute><TourPlanner /></ProtectedRoute>} />
          <Route path="/conservation" element={<ProtectedRoute><ConservationTracker /></ProtectedRoute>} />
          <Route path="/analytics" element={<ProtectedRoute><ExhibitAnalytics /></ProtectedRoute>} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
        </Routes>
      </main>

      <footer className="footer">
        <button onClick={handleUploadClick} className="upload-btn">
          üì§ –ó–∞–≥—Ä—É–∑–∏—Ç—å —Ñ–∞–π–ª
        </button>
        <p>–ú—É–∑–µ–π –∏—Å–∫—É—Å—Å—Ç–≤ ¬© 2024 | React + Node.js –õ–∞–±–æ—Ä–∞—Ç–æ—Ä–Ω–∞—è —Ä–∞–±–æ—Ç–∞</p>
      </footer>
    </div>
  );
}

function ProtectedRoute({ children }) {
  const { user } = useAuth();
  const location = useLocation();

  if (!user) {
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  return children;
}

function App() {
  return (
    <GoogleOAuthProvider clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID || "demo-client-id"}>
      <AuthProvider>
        <Router>
          <MainApp />
        </Router>
      </AuthProvider>
    </GoogleOAuthProvider>
  );
}

export default App;
