import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../components/AuthContext';

const ExhibitCatalog = () => {
  const [exhibits, setExhibits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [sortField, setSortField] = useState('name');
  const [sortOrder, setSortOrder] = useState('asc');
  const { user } = useAuth();
  const navigate = useNavigate();

  // –ó–∞–≥—Ä—É–∑–∫–∞ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤
  useEffect(() => {
    loadExhibits();
  }, []);

  const loadExhibits = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/exhibits');
      const data = await response.json();
      if (data.success) {
        setExhibits(data.exhibits || []);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', error);
    } finally {
      setLoading(false);
    }
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–ª–∏–∫–∞ –ø–æ —Å—Ç—Ä–æ–∫–µ
  const handleRowClick = (exhibitId) => {
    navigate(`/exhibits/${exhibitId}`);
  };

  // –£–¥–∞–ª–µ–Ω–∏–µ —ç–∫—Å–ø–æ–Ω–∞—Ç–∞
  const handleDelete = async (exhibitId, exhibitName, e) => {
    e.stopPropagation(); // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –≤—Å–ø–ª—ã—Ç–∏–µ
    
    if (!window.confirm(`–£–¥–∞–ª–∏—Ç—å —ç–∫—Å–ø–æ–Ω–∞—Ç "${exhibitName}"?`)) {
      return;
    }

    try {
      const userData = JSON.parse(localStorage.getItem('museumUser'));
      const response = await fetch(`http://localhost:5000/api/exhibits/${exhibitId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${userData?.token}`,
          'Content-Type': 'application/json'
        }
      });

      const result = await response.json();
      if (response.ok && result.success) {
        alert('–≠–∫—Å–ø–æ–Ω–∞—Ç —É–¥–∞–ª–µ–Ω');
        loadExhibits(); // –ü–µ—Ä–µ–∑–∞–≥—Ä—É–∂–∞–µ–º —Å–ø–∏—Å–æ–∫
      } else {
        alert(result.message || '–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è');
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞:', error);
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏');
    }
  };

  // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –∏ —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞
  const filteredExhibits = exhibits.filter(exhibit => {
    const matchesSearch = searchTerm === '' || 
      exhibit.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      exhibit.description?.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesCategory = categoryFilter === 'all' || exhibit.category === categoryFilter;
    
    return matchesSearch && matchesCategory;
  });

  // –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞
  const sortedExhibits = [...filteredExhibits].sort((a, b) => {
    let aValue = a[sortField];
    let bValue = b[sortField];
    
    if (sortField === 'createdAt') {
      aValue = new Date(a.createdAt);
      bValue = new Date(b.createdAt);
    }
    
    if (sortOrder === 'asc') {
      return aValue > bValue ? 1 : -1;
    } else {
      return aValue < bValue ? 1 : -1;
    }
  });

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –∏–∫–æ–Ω–∫–∏ —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏
  const getSortIcon = (field) => {
    if (sortField !== field) return '‚ÜïÔ∏è';
    return sortOrder === 'asc' ? '‚Üë' : '‚Üì';
  };

  // –ö–∞—Ç–µ–≥–æ—Ä–∏–∏
  const categories = ['all', 'art', 'history', 'archaeology', 'natural', 'science'];

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤...</p>
      </div>
    );
  }

  return (
    <div className="catalog-container">
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ –∏ –∫–Ω–æ–ø–∫–∏ */}
      <div className="catalog-header">
        <h1>üìã –ö–∞—Ç–∞–ª–æ–≥ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤</h1>
        <div className="header-actions">
          {user && (
            <Link to="/exhibits/new" className="add-exhibit-btn">
              ‚ûï –î–æ–±–∞–≤–∏—Ç—å —ç–∫—Å–ø–æ–Ω–∞—Ç
            </Link>
          )}
          <button onClick={loadExhibits} className="refresh-btn">
            üîÑ –û–±–Ω–æ–≤–∏—Ç—å
          </button>
        </div>
      </div>

      {/* –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
      <div className="control-panel">
        <div className="search-box">
          <input
            type="text"
            placeholder="–ü–æ–∏—Å–∫ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button className="search-btn">üîç</button>
        </div>

        <div className="filters">
          <select 
            value={categoryFilter} 
            onChange={(e) => setCategoryFilter(e.target.value)}
            className="filter-select"
          >
            {categories.map(cat => (
              <option key={cat} value={cat}>
                {cat === 'all' ? '–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏' : 
                 cat === 'art' ? '–ò—Å–∫—É—Å—Å—Ç–≤–æ' :
                 cat === 'history' ? '–ò—Å—Ç–æ—Ä–∏—è' :
                 cat === 'archaeology' ? '–ê—Ä—Ö–µ–æ–ª–æ–≥–∏—è' :
                 cat === 'natural' ? '–ü—Ä–∏—Ä–æ–¥–∞' : '–ù–∞—É–∫–∞'}
              </option>
            ))}
          </select>

          <div className="sort-options">
            <button 
              onClick={() => {
                if (sortField === 'name') {
                  setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
                } else {
                  setSortField('name');
                  setSortOrder('asc');
                }
              }}
              className={`sort-btn ${sortField === 'name' ? 'active' : ''}`}
            >
              –ü–æ –Ω–∞–∑–≤–∞–Ω–∏—é {getSortIcon('name')}
            </button>
            
            <button 
              onClick={() => {
                if (sortField === 'year') {
                  setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
                } else {
                  setSortField('year');
                  setSortOrder('desc');
                }
              }}
              className={`sort-btn ${sortField === 'year' ? 'active' : ''}`}
            >
              –ü–æ –≥–æ–¥—É {getSortIcon('year')}
            </button>
          </div>
        </div>
      </div>

      {/* –¢–∞–±–ª–∏—Ü–∞ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤ */}
      <div className="exhibits-table-container">
        <table className="exhibits-table">
          <thead>
            <tr>
              <th>–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ</th>
              <th>–ù–∞–∑–≤–∞–Ω–∏–µ</th>
              <th>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</th>
              <th>–ì–æ–¥</th>
              <th>–°—Ç–∞—Ç—É—Å</th>
              <th>–°–æ—Å—Ç–æ—è–Ω–∏–µ</th>
              <th>–î–∞—Ç–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è</th>
              <th>–î–µ–π—Å—Ç–≤–∏—è</th>
            </tr>
          </thead>
          <tbody>
            {sortedExhibits.length === 0 ? (
              <tr>
                <td colSpan="8" className="no-data">
                  –≠–∫—Å–ø–æ–Ω–∞—Ç—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã
                </td>
              </tr>
            ) : (
              sortedExhibits.map(exhibit => (
                <tr 
                  key={exhibit._id || exhibit.id} 
                  className="exhibit-row"
                  onClick={() => handleRowClick(exhibit._id || exhibit.id)}
                >
                  <td className="image-cell">
                    <img 
                      src={exhibit.imageUrl || 'https://via.placeholder.com/60x60/4cc9f0/ffffff?text=üñºÔ∏è'} 
                      alt={exhibit.name}
                      className="exhibit-thumb"
                    />
                  </td>
                  <td className="name-cell">
                    <strong>{exhibit.name}</strong>
                    <div className="excerpt">{exhibit.description?.substring(0, 60)}...</div>
                  </td>
                  <td className="category-cell">
                    <span className={`category-badge category-${exhibit.category}`}>
                      {exhibit.category === 'art' ? 'üé® –ò—Å–∫—É—Å—Å—Ç–≤–æ' :
                       exhibit.category === 'history' ? 'üìú –ò—Å—Ç–æ—Ä–∏—è' :
                       exhibit.category === 'archaeology' ? 'üè∫ –ê—Ä—Ö–µ–æ–ª–æ–≥–∏—è' :
                       exhibit.category === 'natural' ? 'üåø –ü—Ä–∏—Ä–æ–¥–∞' : 'üî¨ –ù–∞—É–∫–∞'}
                    </span>
                  </td>
                  <td className="year-cell">{exhibit.year}</td>
                  <td className="status-cell">
                    <span className={`status-badge status-${exhibit.status}`}>
                      {exhibit.status === 'exhibited' ? '–ù–∞ –≤—ã—Å—Ç–∞–≤–∫–µ' :
                       exhibit.status === 'storage' ? '–í —Ö—Ä–∞–Ω–∏–ª–∏—â–µ' :
                       exhibit.status === 'restoration' ? '–ù–∞ —Ä–µ—Å—Ç–∞–≤—Ä–∞—Ü–∏–∏' : '–í –∞—Ä–µ–Ω–¥–µ'}
                    </span>
                  </td>
                  <td className="conservation-cell">
                    <span className={`conservation-badge conservation-${exhibit.conservationState}`}>
                      {exhibit.conservationState === 'excellent' ? '–û—Ç–ª–∏—á–Ω–æ–µ' :
                       exhibit.conservationState === 'good' ? '–•–æ—Ä–æ—à–µ–µ' :
                       exhibit.conservationState === 'satisfactory' ? '–£–¥–æ–≤–ª.' : '–¢—Ä–µ–±—É–µ—Ç –≤–Ω–∏–º–∞–Ω–∏—è'}
                    </span>
                  </td>
                  <td className="date-cell">
                    <div className="date-info">
                      <div className="date-local">
                        üìÖ {exhibit.createdAtLocal || new Date(exhibit.createdAt).toLocaleString('ru-RU')}
                      </div>
                      <div className="date-utc">
                        üåê {exhibit.createdAtUTC || new Date(exhibit.createdAt).toUTCString()}
                      </div>
                    </div>
                  </td>
                  <td className="actions-cell" onClick={(e) => e.stopPropagation()}>
                    <div className="action-buttons">
                      <button 
                        className="action-btn view-btn"
                        onClick={() => navigate(`/exhibits/${exhibit._id || exhibit.id}`)}
                        title="–ü—Ä–æ—Å–º–æ—Ç—Ä"
                      >
                        üëÅÔ∏è
                      </button>
                      
                      {user && (
                        <>
                          <button 
                            className="action-btn edit-btn"
                            onClick={() => navigate(`/exhibits/${exhibit._id || exhibit.id}/edit`)}
                            title="–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å"
                          >
                            ‚úèÔ∏è
                          </button>
                          
                          {user.role === 'admin' && (
                            <button 
                              className="action-btn delete-btn"
                              onClick={(e) => handleDelete(exhibit._id || exhibit.id, exhibit.name, e)}
                              title="–£–¥–∞–ª–∏—Ç—å"
                            >
                              üóëÔ∏è
                            </button>
                          )}
                        </>
                      )}
                    </div>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
      <div className="catalog-footer">
        <div className="stats">
          <span>–í—Å–µ–≥–æ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤: {exhibits.length}</span>
          <span>–û—Ç–æ–±—Ä–∞–∂–µ–Ω–æ: {sortedExhibits.length}</span>
        </div>
      </div>
    </div>
  );
};

export default ExhibitCatalog;
