import { GoogleLogin } from '@react-oauth/google';
import { useAuth } from '../components/AuthContext';
import React from 'react';
import { jwtDecode } from 'jwt-decode';

const GoogleAuthButton = () => {
  const { loginWithGoogle } = useAuth();


  const handleSuccess = async (credentialResponse) => {
  console.log('Google Login Success:', credentialResponse);
  
  try {
    // 1. Декодируем токен Google для получения информации
    const decoded = jwtDecode(credentialResponse.credential);
    console.log('Google пользователь:', decoded);
    
    // 2. Отправляем на ваш сервер для создания сессии
    const response = await fetch('http://localhost:5000/api/auth/google', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ token: credentialResponse.credential })
    });
    
    if (!response.ok) {
      throw new Error('Сервер не отвечает');
    }
    
    const data = await response.json();
    
    if (data.success && data.user) {
      // 3. Сохраняем пользователя с сервера
      const userData = {
        ...data.user,
        googleId: decoded.sub,
        picture: decoded.picture || 'https://via.placeholder.com/150'
      };
      
      // 4. Сохраняем в localStorage
      localStorage.setItem('museumUser', JSON.stringify(userData));
      localStorage.setItem('authToken', userData.token);
      
      // 5. Устанавливаем срок действия токена (24 часа)
      const expiryTime = new Date().getTime() + (24 * 60 * 60 * 1000);
      localStorage.setItem('tokenExpiry', expiryTime.toString());
      
      // 6. Обновляем состояние
      loginWithGoogle(userData);
      
      console.log('✅ Google вход успешен:', userData.username);
      
      // 7. Перенаправляем на главную
      setTimeout(() => {
        window.location.href = '/';
      }, 500);
      
    } else {
      throw new Error(data.message || 'Ошибка сервера');
    }
  } catch (error) {
    console.error('❌ Google OAuth Error:', error);
    alert(`Ошибка входа: ${error.message}`);
  }
};

  const handleError = () => {
    console.error('Google Login Failed');
    alert('Не удалось войти через Google. Проверьте настройки OAuth.');
  };

  return (
    <div className="google-auth-wrapper">
      <div className="google-divider">
        <span>Или войдите через Google</span>
      </div>
      
      <GoogleLogin
        onSuccess={handleSuccess}
        onError={handleError}
        size="large"
        theme="filled_blue"
        text="signin_with"
        shape="rectangular"
        locale="ru"
        width="300"
      />
      
      <div className="google-note">
        <p>Для работы требуется Client ID в .env файле</p>
      </div>
    </div>
  );
};

export default GoogleAuthButton;
