import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const ExhibitForm = ({ mode = 'create' }) => {
  const navigate = useNavigate();
  const { id } = useParams();
  
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: 'art',
    location: { hall: '', room: '', floor: '' },
    status: 'exhibited',
    conservationState: 'good',
    imageUrl: '',
    year: '',
    materials: [''],
    dimensions: { height: '', width: '', depth: '' },
    value: ''
  });
  
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  // Используем useCallback для стабильной ссылки на функцию
  const loadExhibit = useCallback(async () => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/exhibits/${id}`);
      if (response.ok) {
        const data = await response.json();
        setFormData({
          ...data,
          year: data.year || '',
          materials: data.materials || [''],
          dimensions: data.dimensions || { height: '', width: '', depth: '' },
          location: data.location || { hall: '', room: '', floor: '' }
        });
      }
    } catch (error) {
      console.error('Ошибка загрузки:', error);
      alert('Не удалось загрузить данные экспоната');
    } finally {
      setLoading(false);
    }
  }, [id]); // Зависимость от id

  useEffect(() => {
    if (mode === 'edit' && id) {
      loadExhibit();
    }
  }, [mode, id, loadExhibit]);

  const validateForm = () => {
    const newErrors = {};

    // Проверка обязательных полей
    if (!formData.name.trim()) {
      newErrors.name = 'Название обязательно';
    } else if (formData.name.length < 3) {
      newErrors.name = 'Название должно быть не менее 3 символов';
    }

    if (!formData.description.trim()) {
      newErrors.description = 'Описание обязательно';
    } else if (formData.description.length < 10) {
      newErrors.description = 'Описание должно быть не менее 10 символов';
    }

    if (!formData.year) {
      newErrors.year = 'Год обязателен';
    } else if (isNaN(formData.year) || formData.year < -10000 || formData.year > new Date().getFullYear()) {
      newErrors.year = 'Неверный год';
    }

    if (!formData.location.hall.trim()) {
      newErrors['location.hall'] = 'Зал обязателен';
    }

    // Проверка материалов
    const validMaterials = formData.materials.filter(m => m.trim());
    if (validMaterials.length === 0) {
      newErrors.materials = 'Укажите хотя бы один материал';
    }

    // Проверка размеров
    if (formData.dimensions.height && (isNaN(formData.dimensions.height) || formData.dimensions.height <= 0)) {
      newErrors['dimensions.height'] = 'Неверная высота';
    }
    if (formData.dimensions.width && (isNaN(formData.dimensions.width) || formData.dimensions.width <= 0)) {
      newErrors['dimensions.width'] = 'Неверная ширина';
    }
    if (formData.dimensions.depth && (isNaN(formData.dimensions.depth) || formData.dimensions.depth <= 0)) {
      newErrors['dimensions.depth'] = 'Неверная глубина';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.startsWith('location.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        location: {
          ...prev.location,
          [field]: value
        }
      }));
    } else if (name.startsWith('dimensions.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        dimensions: {
          ...prev.dimensions,
          [field]: value
        }
      }));
    } else if (name.startsWith('materials.')) {
      const index = parseInt(name.split('.')[1]);
      const newMaterials = [...formData.materials];
      newMaterials[index] = value;
      setFormData(prev => ({
        ...prev,
        materials: newMaterials
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }

    // Очищаем ошибку при изменении поля
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: undefined }));
    }
  };

  const addMaterial = () => {
    setFormData(prev => ({
      ...prev,
      materials: [...prev.materials, '']
    }));
  };

  const removeMaterial = (index) => {
    if (formData.materials.length > 1) {
      const newMaterials = formData.materials.filter((_, i) => i !== index);
      setFormData(prev => ({
        ...prev,
        materials: newMaterials
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      alert('Пожалуйста, исправьте ошибки в форме');
      return;
    }

    setSubmitting(true);
    
    try {
      const user = JSON.parse(localStorage.getItem('museumUser'));
      if (!user || !user.token) {
        alert('Требуется авторизация');
        navigate('/login');
        return;
      }

      // Подготовка данных
      const submitData = {
        ...formData,
        year: parseInt(formData.year),
        materials: formData.materials.filter(m => m.trim()),
        dimensions: {
          height: formData.dimensions.height ? parseInt(formData.dimensions.height) : undefined,
          width: formData.dimensions.width ? parseInt(formData.dimensions.width) : undefined,
          depth: formData.dimensions.depth ? parseInt(formData.dimensions.depth) : undefined
        }
      };

      const url = mode === 'create' 
        ? 'http://localhost:5000/api/exhibits'
        : `http://localhost:5000/api/exhibits/${id}`;

      const method = mode === 'create' ? 'POST' : 'PUT';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify(submitData)
      });

      if (response.ok) {
        const data = await response.json();
        alert(mode === 'create' ? 'Экспонат создан!' : 'Экспонат обновлен!');
        navigate(`/exhibits/${data._id || data.id}`);
      } else {
        const errorData = await response.json();
        alert(errorData.message || 'Ошибка при сохранении');
      }
    } catch (error) {
      console.error('Ошибка отправки формы:', error);
      alert('Ошибка при сохранении экспоната');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Загрузка данных...</p>
      </div>
    );
  }

  return (
    <div className="exhibit-form-container">
      <div className="form-header">
        <h2>{mode === 'create' ? 'Создание нового экспоната' : 'Редактирование экспоната'}</h2>
        <p>Заполните все обязательные поля (*)</p>
      </div>

      <form onSubmit={handleSubmit} className="exhibit-form">
        <div className="form-section">
          <h3>📋 Основная информация</h3>
          
          <div className="form-group">
            <label htmlFor="name">
              Название экспоната *
              {errors.name && <span className="error-message"> — {errors.name}</span>}
            </label>
            <input
              id="name"
              name="name"
              type="text"
              value={formData.name}
              onChange={handleChange}
              placeholder="Например: Древнегреческая амфора"
              className={errors.name ? 'error' : ''}
              maxLength={100}
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">
              Описание *
              {errors.description && <span className="error-message"> — {errors.description}</span>}
            </label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Подробное описание экспоната..."
              rows={4}
              className={errors.description ? 'error' : ''}
              maxLength={1000}
            />
            <div className="char-count">{formData.description.length}/1000</div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="year">
                Год *
                {errors.year && <span className="error-message"> — {errors.year}</span>}
              </label>
              <input
                id="year"
                name="year"
                type="number"
                value={formData.year}
                onChange={handleChange}
                placeholder="Например: -500"
                className={errors.year ? 'error' : ''}
                min="-10000"
                max={new Date().getFullYear()}
              />
            </div>

            <div className="form-group">
              <label htmlFor="category">Категория *</label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
              >
                <option value="art">Искусство</option>
                <option value="history">История</option>
                <option value="archaeology">Археология</option>
                <option value="natural">Природа</option>
                <option value="science">Наука</option>
              </select>
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>📍 Местоположение</h3>
          
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="location.hall">
                Зал *
                {errors['location.hall'] && <span className="error-message"> — {errors['location.hall']}</span>}
              </label>
              <input
                id="location.hall"
                name="location.hall"
                type="text"
                value={formData.location.hall}
                onChange={handleChange}
                placeholder="Например: Античное искусство"
                className={errors['location.hall'] ? 'error' : ''}
              />
            </div>

            <div className="form-group">
              <label htmlFor="location.room">Комната</label>
              <input
                id="location.room"
                name="location.room"
                type="text"
                value={formData.location.room}
                onChange={handleChange}
                placeholder="Например: 101"
              />
            </div>

            <div className="form-group">
              <label htmlFor="location.floor">Этаж</label>
              <input
                id="location.floor"
                name="location.floor"
                type="number"
                value={formData.location.floor}
                onChange={handleChange}
                placeholder="Например: 1"
                min="0"
                max="10"
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>⚙️ Статус и состояние</h3>
          
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="status">Статус</label>
              <select
                id="status"
                name="status"
                value={formData.status}
                onChange={handleChange}
              >
                <option value="exhibited">На выставке</option>
                <option value="storage">В хранилище</option>
                <option value="restoration">На реставрации</option>
                <option value="loaned">В аренде</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="conservationState">Состояние сохранности</label>
              <select
                id="conservationState"
                name="conservationState"
                value={formData.conservationState}
                onChange={handleChange}
              >
                <option value="excellent">Отличное</option>
                <option value="good">Хорошее</option>
                <option value="satisfactory">Удовлетворительное</option>
                <option value="needs_attention">Требует внимания</option>
              </select>
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>🔧 Материалы</h3>
          {errors.materials && (
            <div className="error-message">{errors.materials}</div>
          )}
          
          {formData.materials.map((material, index) => (
            <div key={index} className="form-row material-row">
              <div className="form-group" style={{ flex: 1 }}>
                <input
                  name={`materials.${index}`}
                  type="text"
                  value={material}
                  onChange={handleChange}
                  placeholder="Например: керамика, золото, мрамор..."
                />
              </div>
              {formData.materials.length > 1 && (
                <button
                  type="button"
                  onClick={() => removeMaterial(index)}
                  className="remove-btn"
                  title="Удалить материал"
                >
                  🗑️
                </button>
              )}
            </div>
          ))}
          
          <button
            type="button"
            onClick={addMaterial}
            className="add-btn"
          >
            + Добавить материал
          </button>
        </div>

        <div className="form-section">
          <h3>📏 Размеры (в см)</h3>
          
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="dimensions.height">Высота</label>
              <input
                id="dimensions.height"
                name="dimensions.height"
                type="number"
                value={formData.dimensions.height}
                onChange={handleChange}
                placeholder="Например: 45"
                min="0"
                className={errors['dimensions.height'] ? 'error' : ''}
              />
              {errors['dimensions.height'] && (
                <div className="error-message">{errors['dimensions.height']}</div>
              )}
            </div>

            <div className="form-group">
              <label htmlFor="dimensions.width">Ширина</label>
              <input
                id="dimensions.width"
                name="dimensions.width"
                type="number"
                value={formData.dimensions.width}
                onChange={handleChange}
                placeholder="Например: 30"
                min="0"
                className={errors['dimensions.width'] ? 'error' : ''}
              />
            </div>

            <div className="form-group">
              <label htmlFor="dimensions.depth">Глубина</label>
              <input
                id="dimensions.depth"
                name="dimensions.depth"
                type="number"
                value={formData.dimensions.depth}
                onChange={handleChange}
                placeholder="Например: 30"
                min="0"
                className={errors['dimensions.depth'] ? 'error' : ''}
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h3>🖼️ Изображение</h3>
          
          <div className="form-group">
            <label htmlFor="imageUrl">URL изображения</label>
            <input
              id="imageUrl"
              name="imageUrl"
              type="url"
              value={formData.imageUrl}
              onChange={handleChange}
              placeholder="https://example.com/image.jpg"
            />
            {formData.imageUrl && (
              <div className="image-preview">
                <img 
                  src={formData.imageUrl} 
                  alt="Предпросмотр" 
                  onError={(e) => e.target.style.display = 'none'}
                />
              </div>
            )}
          </div>
        </div>

        <div className="form-section">
          <h3>💰 Оценка ценности</h3>
          
          <div className="form-group">
            <label htmlFor="value">Описание ценности</label>
            <textarea
              id="value"
              name="value"
              value={formData.value}
              onChange={handleChange}
              placeholder="Историческая, художественная или научная ценность..."
              rows={3}
            />
          </div>
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={() => navigate('/exhibits')}
            className="cancel-btn"
            disabled={submitting}
          >
            Отмена
          </button>
          
          <button
            type="submit"
            disabled={submitting}
            className="submit-btn"
          >
            {submitting ? 'Сохранение...' : (mode === 'create' ? 'Создать экспонат' : 'Сохранить изменения')}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ExhibitForm;
