import React, { useState, useRef } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../components/AuthContext';

const Navigation = ({ 
  user, 
  onSearchSubmit, 
  searchInputRef, 
  onSearchChange, 
  searchQuery 
}) => {
  const location = useLocation();
  const navigate = useNavigate();
  const { logout } = useAuth();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuRef = useRef(null);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleClickOutside = (e) => {
    if (menuRef.current && !menuRef.current.contains(e.target)) {
      setIsMenuOpen(false);
    }
  };

  React.useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <nav className="navigation">
      <div className="nav-container">
      
        <div className="nav-brand">
          <Link to="/" className="logo-link">
            <span className="logo-icon">üèõÔ∏è</span>
            <span className="logo-text">–¶–∏—Ñ—Ä–æ–≤–æ–π –ú—É–∑–µ–π</span>
          </Link>
        </div>

        <button 
          className="menu-toggle" 
          onClick={toggleMenu}
          aria-label="–û—Ç–∫—Ä—ã—Ç—å –º–µ–Ω—é"
        >
          <span className="menu-icon">‚ò∞</span>
        </button>

        <div className={`nav-links ${isMenuOpen ? 'open' : ''}`} ref={menuRef}>
                   
          <Link 
            to="/exhibits" 
            className={`nav-link ${location.pathname.startsWith('/exhibits') ? 'active' : ''}`}
            onClick={() => setIsMenuOpen(false)}
          >
            üñºÔ∏è –≠–∫—Å–ø–æ–Ω–∞—Ç—ã
          </Link>
          
          {user && (
            <>
              <Link 
                to="/scanner" 
                className={`nav-link ${location.pathname === '/scanner' ? 'active' : ''}`}
                onClick={() => setIsMenuOpen(false)}
              >
                üì± –°–∫–∞–Ω–µ—Ä
              </Link>
              
              <Link 
                to="/tours" 
                className={`nav-link ${location.pathname === '/tours' ? 'active' : ''}`}
                onClick={() => setIsMenuOpen(false)}
              >
                üó∫Ô∏è –≠–∫—Å–∫—É—Ä—Å–∏–∏
              </Link>
              
              <Link 
                to="/analytics" 
                className={`nav-link ${location.pathname === '/analytics' ? 'active' : ''}`}
                onClick={() => setIsMenuOpen(false)}
              >
                üìä –ê–Ω–∞–ª–∏—Ç–∏–∫–∞
              </Link>
              
              {user.role === 'admin' && (
                <Link 
                  to="/conservation" 
                  className={`nav-link ${location.pathname === '/conservation' ? 'active' : ''}`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  üõ°Ô∏è –ö–æ–Ω—Å–µ—Ä–≤–∞—Ü–∏—è
                </Link>
              )}
            </>
          )}
        </div>
       
        <div className="user-section">
          {user ? (
            <div className="user-info">
              <div className="user-avatar">
                {user.username?.charAt(0).toUpperCase() || 'U'}
              </div>
              <div className="user-details">
                <span className="username">{user.username}</span>
                <span className="user-role">{user.role}</span>
              </div>
              <div className="user-actions">
                <button onClick={handleLogout} className="logout-button">
                  –í—ã–π—Ç–∏
                </button>
              </div>
            </div>
          ) : (
            <div className="auth-buttons">
              <Link to="/login" className="auth-button login-button">
                –í–æ–π—Ç–∏
              </Link>
              <Link to="/register" className="auth-button register-button">
                –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è
              </Link>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
