import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../components/AuthContext';
import { analyzeWithOllama } from '../components/ollamaService';

const ExhibitDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  
  const [exhibit, setExhibit] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [aiAnalysis, setAiAnalysis] = useState(null);
  const [analyzing, setAnalyzing] = useState(false);
  const [lighting, setLighting] = useState(100);

 const loadExhibits = useCallback(async (force = false) => {
  const now = Date.now();
  if (!force && now - lastLoadTime < 5000 && museumState.exhibits.length > 0) {
    console.log('‚è∞ –°–ª–∏—à–∫–æ–º —á–∞—Å—Ç—ã–π –≤—ã–∑–æ–≤, –ø—Ä–æ–ø—É—Å–∫–∞—é');
    return;
  }
  
  if (isLoading) {
    console.log('‚è≥ –ó–∞–≥—Ä—É–∑–∫–∞ —É–∂–µ –≤—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è');
    return;
  }
  
  console.log('üîÑ –ù–∞—á–∏–Ω–∞—é –∑–∞–≥—Ä—É–∑–∫—É —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤...');
  setIsLoading(true);
  
  try {
    // –°–Ω–∞—á–∞–ª–∞ –ø—Ä–æ–±—É–µ–º –æ—Å–Ω–æ–≤–Ω–æ–π endpoint
    let response = await fetch('http://localhost:5000/api/bits', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('authToken') || 'demo-token'}`
      }
    });
    
    console.log('üì° –û—Ç–≤–µ—Ç –æ—Ç /api/bits:', response.status);
    
    // –ï—Å–ª–∏ –Ω–µ –ø–æ–ª—É—á–∞–µ—Ç—Å—è, –ø—Ä–æ–±—É–µ–º —Ç–µ—Å—Ç–æ–≤—ã–π endpoint
    if (!response.ok) {
      console.log('‚ö†Ô∏è –û—Å–Ω–æ–≤–Ω–æ–π endpoint –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç, –ø—Ä–æ–±—É—é —Ç–µ—Å—Ç–æ–≤—ã–π...');
      response = await fetch('http://localhost:5000/api/test-exhibits');
    }
    
    if (response.ok) {
      const data = await response.json();
      console.log('üì¶ –î–∞–Ω–Ω—ã–µ –ø–æ–ª—É—á–µ–Ω—ã:', {
        success: data.success,
        count: data.bits?.length,
        source: data.message
      });
      
      if (data.success && isMounted.current) {
        dispatch({ type: 'SET_EXHIBITS', payload: data.bits || [] });
        setLastLoadTime(Date.now());
        
        if (museumState.exhibits.length > 0 || force) {
          addNotification(`–ó–∞–≥—Ä—É–∂–µ–Ω–æ ${data.bits?.length || 0} —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤`, 'success');
        }
      }
    } else {
      console.error('‚ùå –û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞:', response.status);
      addNotification('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤', 'error');
    }
  } catch (error) {
    console.error('‚ùå –û—à–∏–±–∫–∞ —Å–µ—Ç–∏:', error);
    addNotification('–û—à–∏–±–∫–∞ —Å–µ—Ç–∏ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤', 'error');
  } finally {
    if (isMounted.current) {
      setIsLoading(false);
      console.log('üèÅ –ó–∞–≥—Ä—É–∑–∫–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∞');
    }
  }
}, [isLoading, lastLoadTime, museumState.exhibits.length, addNotification]);

  useEffect(() => {
    loadExhibit();
  }, [id]);

  const handleDelete = async () => {
    if (!window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç–æ—Ç —ç–∫—Å–ø–æ–Ω–∞—Ç?')) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/bits/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${user?.token}`
        }
      });

      if (response.ok) {
        alert('–≠–∫—Å–ø–æ–Ω–∞—Ç —É–¥–∞–ª–µ–Ω');
        navigate('/exhibits');
      } else {
        const data = await response.json();
        alert(data.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏');
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è:', error);
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ —ç–∫—Å–ø–æ–Ω–∞—Ç–∞');
    }
  };

  const handleAiAnalysis = async (type = 'description') => {
    if (!exhibit) return;
    
    setAnalyzing(true);
    try {
      const result = await analyzeWithOllama(exhibit, type);
      setAiAnalysis(result);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ AI –∞–Ω–∞–ª–∏–∑–∞:', error);
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∞–Ω–∞–ª–∏–∑–µ —á–µ—Ä–µ–∑ Ollama');
    } finally {
      setAnalyzing(false);
    }
  };

  const handleLightingChange = (e) => {
    setLighting(e.target.value);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ —ç–∫—Å–ø–æ–Ω–∞—Ç–∞...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>‚ùå –û—à–∏–±–∫–∞</h2>
        <p>{error}</p>
        <Link to="/exhibits" className="back-link">
          ‚Üê –í–µ—Ä–Ω—É—Ç—å—Å—è –∫ –∫–∞—Ç–∞–ª–æ–≥—É
        </Link>
      </div>
    );
  }

  if (!exhibit) {
    return (
      <div className="not-found">
        <h2>–≠–∫—Å–ø–æ–Ω–∞—Ç –Ω–µ –Ω–∞–π–¥–µ–Ω</h2>
        <Link to="/exhibits">‚Üê –í–µ—Ä–Ω—É—Ç—å—Å—è –∫ –∫–∞—Ç–∞–ª–æ–≥—É</Link>
      </div>
    );
  }

  return (
    <div className="exhibit-detail">
      <div className="detail-header">
        <div>
          <h1>{exhibit.name}</h1>
          <div className="exhibit-meta">
            <span className="category-badge">{exhibit.category}</span>
            <span className="year-badge">{exhibit.year} –≥.</span>
          </div>
        </div>
        
        <div className="detail-actions">
          <Link to="/exhibits" className="back-btn">
            ‚Üê –ù–∞–∑–∞–¥
          </Link>
          
          {/* –¢–æ–ª—å–∫–æ –¥–ª—è –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–æ–≤ */}
          {user && user.role === 'admin' && (
            <>
              <Link to={`/exhibits/${id}/edit`} className="edit-btn">
                ‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
              </Link>
              <button onClick={handleDelete} className="delete-btn">
                üóëÔ∏è –£–¥–∞–ª–∏—Ç—å
              </button>
            </>
          )}
          
          {/* –î–ª—è –æ–±—ã—á–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π - —Ç–æ–ª—å–∫–æ –ø—Ä–æ—Å–º–æ—Ç—Ä */}
          {user && user.role !== 'admin' && (
            <span className="user-note">
              üë§ –†–µ–∂–∏–º –ø—Ä–æ—Å–º–æ—Ç—Ä–∞
            </span>
          )}
        </div>
      </div>

      <div className="detail-content">
        <div className="exhibit-media">
          <div className="image-container">
            <img 
              src={exhibit.imageUrl || 'https://via.placeholder.com/600x400/4cc9f0/ffffff?text=–≠–∫—Å–ø–æ–Ω–∞—Ç'} 
              alt={exhibit.name}
              style={{ filter: `brightness(${lighting}%)` }}
            />
          </div>
          
          <div className="media-controls">
            <div className="control-group">
              <label htmlFor="lighting">üí° –û—Å–≤–µ—â–µ–Ω–∏–µ: {lighting}%</label>
              <input
                id="lighting"
                type="range"
                min="10"
                max="150"
                value={lighting}
                onChange={handleLightingChange}
              />
            </div>
            
          </div>
        </div>

        <div className="exhibit-info">
          <div className="info-section">
            <h3>üìã –û–ø–∏—Å–∞–Ω–∏–µ</h3>
            <p>{exhibit.description || '–û–ø–∏—Å–∞–Ω–∏–µ –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç'}</p>
          </div>

          <div className="info-section">
            <h3>üìç –ú–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏–µ</h3>
            <div className="location-details">
              <p><strong>–ó–∞–ª:</strong> {exhibit.location?.hall || '–ù–µ —É–∫–∞–∑–∞–Ω'}</p>
              <p><strong>–ö–æ–º–Ω–∞—Ç–∞:</strong> {exhibit.location?.room || '–ù–µ —É–∫–∞–∑–∞–Ω–∞'}</p>
              <p><strong>–≠—Ç–∞–∂:</strong> {exhibit.location?.floor || '–ù–µ —É–∫–∞–∑–∞–Ω'}</p>
            </div>
          </div>

          {exhibit.assignedEmployee && (
            <div className="info-section">
              <h3>üë®‚Äçüíº –û—Ç–≤–µ—Ç—Å—Ç–≤–µ–Ω–Ω—ã–π —Å–æ—Ç—Ä—É–¥–Ω–∏–∫</h3>
              <div className="employee-card">
                <div className="employee-avatar">
                  {exhibit.assignedEmployee.name?.charAt(0) || '–°'}
                </div>
                <div className="employee-details">
                  <h4>{exhibit.assignedEmployee.name || '–ù–µ —É–∫–∞–∑–∞–Ω'}</h4>
