import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../components/AuthContext';

const Contacts = () => {
  const { user } = useAuth();
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [clickCount, setClickCount] = useState(0);
  const [hoveredEmployee, setHoveredEmployee] = useState(null);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
 
  useEffect(() => {
    loadEmployees();
  }, []);

  const loadEmployees = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await fetch('http://localhost:5000/api/employees', {
        headers: {
          'Authorization': user?.token ? `Bearer ${user.token}` : ''
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setEmployees(data.employees || []);
        } else {
          setError(data.message || '–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –¥–∞–Ω–Ω—ã–µ');
        }
      } else {
        throw new Error(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏: ${response.status}`);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–æ–≤:', error);
      setError(error.message);
      
      setEmployees([
        { 
          _id: '1', 
          name: '–ò–≤–∞–Ω–æ–≤–∞ –ê–Ω–Ω–∞ –°–µ—Ä–≥–µ–µ–≤–Ω–∞', 
          position: '–ì–ª–∞–≤–Ω—ã–π –∫—É—Ä–∞—Ç–æ—Ä', 
          department: '–û—Ç–¥–µ–ª –∂–∏–≤–æ–ø–∏—Å–∏',
          email: 'ivanova@museum.ru',
          phone: '+7 (495) 123-45-67'
        },
        { 
          _id: '2', 
          name: '–ü–µ—Ç—Ä–æ–≤ –î–º–∏—Ç—Ä–∏–π –í–ª–∞–¥–∏–º–∏—Ä–æ–≤–∏—á', 
          position: '–†–µ—Å—Ç–∞–≤—Ä–∞—Ç–æ—Ä', 
          department: '–†–µ—Å—Ç–∞–≤—Ä–∞—Ü–∏–æ–Ω–Ω—ã–π –æ—Ç–¥–µ–ª',
          email: 'petrov@museum.ru',
          phone: '+7 (495) 123-45-68'
        },
        { 
          _id: '3', 
          name: '–°–∏–¥–æ—Ä–æ–≤–∞ –ï–ª–µ–Ω–∞ –ú–∏—Ö–∞–π–ª–æ–≤–Ω–∞', 
          position: '–≠–∫—Å–∫—É—Ä—Å–æ–≤–æ–¥', 
          department: '–≠–∫—Å–∫—É—Ä—Å–∏–æ–Ω–Ω—ã–π –æ—Ç–¥–µ–ª',
          email: 'sidorova@museum.ru',
          phone: '+7 (495) 123-45-69'
        },
        { 
          _id: '4', 
          name: '–ö—É–∑–Ω–µ—Ü–æ–≤ –ê–ª–µ–∫—Å–µ–π –ü–µ—Ç—Ä–æ–≤–∏—á', 
          position: '–°–º–æ—Ç—Ä–∏—Ç–µ–ª—å', 
          department: '–°–ª—É–∂–±–∞ –±–µ–∑–æ–ø–∞—Å–Ω–æ—Å—Ç–∏',
          email: 'kuznetsov@museum.ru',
          phone: '+7 (495) 123-45-70'
        },
        { 
          _id: '5', 
          name: '–ú–æ—Ä–æ–∑–æ–≤–∞ –û–ª—å–≥–∞ –í–∞—Å–∏–ª—å–µ–≤–Ω–∞', 
          position: '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä', 
          department: '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ü–∏—è',
          email: 'morozova@museum.ru',
          phone: '+7 (495) 123-45-71'
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleHeaderClick = () => {
    console.log('–ö–ª–∏–∫–Ω—É–ª–∏ –ø–æ –∑–∞–≥–æ–ª–æ–≤–∫—É —Å—Ç—Ä–∞–Ω–∏—Ü—ã');
    setClickCount(prev => prev + 1);
    alert(`–ó–∞–≥–æ–ª–æ–≤–æ–∫ –∫–ª–∏–∫–Ω—É—Ç! –í—Å–µ–≥–æ –∫–ª–∏–∫–æ–≤: ${clickCount + 1}`);
  };

  const handleEmployeeMouseEnter = (employee) => {
    console.log(`–ù–∞–≤–µ–ª–∏ –Ω–∞ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–∞: ${employee.name}`);
    setHoveredEmployee(employee._id);
  };

  const handleEmployeeMouseLeave = () => {
    console.log('–£–±—Ä–∞–ª–∏ –º—ã—à—å —Å —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–∞');
    setHoveredEmployee(null);
  };

  const handleEmployeeDoubleClick = (employee) => {
    console.log(`–î–≤–æ–π–Ω–æ–π –∫–ª–∏–∫ –ø–æ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫—É: ${employee.name}`);
    setSelectedEmployee(employee);
    alert(`–í—ã –≤—ã–±—Ä–∞–ª–∏: ${employee.name}\n–î–æ–ª–∂–Ω–æ—Å—Ç—å: ${employee.position}`);
  };

  const handleSearchChange = (e) => {
    console.log('–ò–∑–º–µ–Ω—ë–Ω –ø–æ–∏—Å–∫–æ–≤—ã–π –∑–∞–ø—Ä–æ—Å:', e.target.value);
    setSearchQuery(e.target.value);
  };

  const handleRightClick = (e, employee) => {
    e.preventDefault(); 
    console.log(`–ü—Ä–∞–≤–∞—è –∫–Ω–æ–ø–∫–∞ –º—ã—à–∏ –Ω–∞: ${employee.name}`);
    alert(`–ë—ã—Å—Ç—Ä–æ–µ –º–µ–Ω—é –¥–ª—è: ${employee.name}\nEmail: ${employee.email}`);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ —Å–ø–∏—Å–∫–∞ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–æ–≤...</p>
      </div>
    );
  }

  return (
    <div className="contacts-page">
    
      <div className="page-header" onClick={handleHeaderClick} style={{ cursor: 'pointer' }}>
        <h1>üìû –ö–æ–Ω—Ç–∞–∫—Ç—ã</h1>
        <p>–°–ø–∏—Å–æ–∫ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–æ–≤ –º—É–∑–µ—è (–∫–ª–∏–∫–Ω–∏—Ç–µ –∑–¥–µ—Å—å! –ö–ª–∏–∫–æ–≤: {clickCount})</p>
      </div>

      {error && (
        <div className="error-message">
          <p>{error}</p>
          <p>–û—Ç–æ–±—Ä–∞–∂–∞—é—Ç—Å—è –¥–µ–º–æ-–¥–∞–Ω–Ω—ã–µ</p>
        </div>
      )}

      <div className="contacts-info">
        <div className="contacts-overview">
          <h2>–ú—É–∑–µ–π "–¶–∏—Ñ—Ä–æ–≤–æ–µ –Ω–∞—Å–ª–µ–¥–∏–µ"</h2>
          <p><strong>–ê–¥—Ä–µ—Å:</strong> –≥. –ú–∏–Ω—Å–∫ —É–ª. –ú—É–∑–µ–π–Ω–∞—è, –¥. 1</p>
          <p><strong>–¢–µ–ª–µ—Ñ–æ–Ω:</strong> +375 (44) 123-45-66</p>
          <p><strong>Email:</strong> info@museum.ru</p>
          <p><strong>–ß–∞—Å—ã —Ä–∞–±–æ—Ç—ã:</strong> 10:00 - 20:00 (–±–µ–∑ –≤—ã—Ö–æ–¥–Ω—ã—Ö)</p>
        </div>

        <div className="contacts-stats">
          <div className="stat-card">
            <h3>{employees.length}</h3>
            <p>–í—Å–µ–≥–æ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–æ–≤</p>
          </div>
          <div className="stat-card">
            <h3>7</h3>
            <p>–û—Ç–¥–µ–ª–æ–≤</p>
          </div>
          <div className="stat-card">
            <h3>24/7</h3>
            <p>–ü–æ–¥–¥–µ—Ä–∂–∫–∞</p>
          </div>
        </div>
      </div>

      <div className="search-section" style={{ margin: '20px 0' }}>
        <input
          type="text"
          placeholder="üîç –ü–æ–∏—Å–∫ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–æ–≤ –ø–æ –∏–º–µ–Ω–∏ –∏–ª–∏ –æ—Ç–¥–µ–ª—É..."
          value={searchQuery}
          onChange={handleSearchChange}
          style={{
            padding: '10px',
            width: '100%',
            maxWidth: '400px',
            borderRadius: '8px',
            border: '1px solid #ccc'
          }}
        />
        <p style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
          –í–≤–µ–¥–∏—Ç–µ —Ç–µ–∫—Å—Ç –¥–ª—è –ø–æ–∏—Å–∫–∞. –¢–µ–∫—É—â–∏–π –∑–∞–ø—Ä–æ—Å: "{searchQuery}"
        </p>
      </div>

      <div className="employees-section">
        <h2>üë• –°–æ—Ç—Ä—É–¥–Ω–∏–∫–∏ –º—É–∑–µ—è</h2>
        
        <div className="departments-container">
          {Array.from(new Set(employees.map(e => e.department))).map(department => {
            const deptEmployees = employees.filter(e => e.department === department);
            
            const filteredEmployees = deptEmployees.filter(employee => 
              employee.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
              employee.position.toLowerCase().includes(searchQuery.toLowerCase()) ||
              employee.department.toLowerCase().includes(searchQuery.toLowerCase())
            );

            if (filteredEmployees.length === 0) return null;

            return (
              <div key={department} className="department-card">
                <h3>{department}</h3>
                <div className="employees-list">
                  {filteredEmployees.map(employee => (
                    <div 
                      key={employee._id} 
                      className="employee-card"
                      style={{
                        backgroundColor: hoveredEmployee === employee._id ? '#f0f8ff' : 'white',
                        border: selectedEmployee?._id === employee._id ? '2px solid #4cc9f0' : '1px solid #ddd',
                        transition: 'all 0.3s ease',
                        cursor: 'pointer'
                      }}
                      
                      onMouseEnter={() => handleEmployeeMouseEnter(employee)}
                      onMouseLeave={handleEmployeeMouseLeave}
                     
                      onDoubleClick={() => handleEmployeeDoubleClick(employee)}
                     
                      onContextMenu={(e) => handleRightClick(e, employee)}
                    >
                      <div className="employee-header">
                        <div className="employee-avatar">
                          {employee.name?.charAt(0) || '–°'}
                        </div>
                        <div className="employee-info">
                          <h4>{employee.name}</h4>
                          <p className="employee-position">{employee.position}</p>
                        </div>
                      </div>
                      <div className="employee-contact">
                        {employee.email && (
                          <p><strong>Email:</strong> <a href={`mailto:${employee.email}`}>{employee.email}</a></p>
                        )}
                        {employee.phone && (
                          <p><strong>–¢–µ–ª–µ—Ñ–æ–Ω:</strong> <a href={`tel:${employee.phone}`}>{employee.phone}</a></p>
                        )}
                        <p><strong>–û—Ç–¥–µ–ª:</strong> {employee.department}</p>
                        
                        {hoveredEmployee === employee._id && (
                          <div style={{
                            fontSize: '11px',
                            color: '#4cc9f0',
                            marginTop: '5px',
                            padding: '3px',
                            backgroundColor: '#e6f7ff',
                            borderRadius: '3px'
                          }}>
                            üí° –î–≤–æ–π–Ω–æ–π –∫–ª–∏–∫ –¥–ª—è –≤—ã–±–æ—Ä–∞ ‚Ä¢ –ü—Ä–∞–≤–∞—è –∫–Ω–æ–ø–∫–∞ –¥–ª—è –±—ã—Å—Ç—Ä–æ–≥–æ –¥–æ—Å—Ç—É–ø–∞
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            );
          })}
        </div>
      </div>

     
      <div className="back-section" style={{ marginTop: '30px' }}>
        <Link to="/" className="back-link">
          ‚Üê –í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞ –≥–ª–∞–≤–Ω—É—é
        </Link>
      </div>

      <div style={{
        marginTop: '40px',
        padding: '15px',
        backgroundColor: '#f8f9fa',
        borderRadius: '8px',
        fontSize: '14px',
        borderLeft: '4px solid #4cc9f0'
      }}>
        <h4>üõ†Ô∏è –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π –Ω–∞ —ç—Ç–æ–π —Å—Ç—Ä–∞–Ω–∏—Ü–µ:</h4>
        <ol>
          <li><strong>onClick</strong> - –∫–ª–∏–∫ –ø–æ –∑–∞–≥–æ–ª–æ–≤–∫—É (—Å—á–∏—Ç–∞–µ—Ç –∫–ª–∏–∫–∏)</li>
          <li><strong>onMouseEnter/Leave</strong> - –ø–æ–¥—Å–≤–µ—Ç–∫–∞ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–æ–≤</li>
          <li><strong>onMouseLeave</strong> - –ø–æ–¥—Å–≤–µ—Ç–∫–∞ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–æ–≤</li>
          <li><strong>onDoubleClick</strong> - –≤—ã–±–æ—Ä —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–∞ –¥–≤–æ–π–Ω—ã–º –∫–ª–∏–∫–æ–º</li>
          <li><strong>onChange</strong> - –ø–æ–∏—Å–∫ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–æ–≤</li>
          <li><strong>onContextMenu</strong> - –ø—Ä–∞–≤–∞—è –∫–Ω–æ–ø–∫–∞ –º—ã—à–∏ –Ω–∞ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–µ</li>
        </ol>
      </div>
    </div>
  );
};

export default Contacts;
