import { GoogleLogin } from '@react-oauth/google';
import { useAuth } from '../components/AuthContext';
import React from 'react';
import { jwtDecode } from 'jwt-decode';

const GoogleAuthButton = () => {
  const { loginWithGoogle } = useAuth();

  const handleSuccess = async (credentialResponse) => {
    console.log('Google Login Success:', credentialResponse);
    
    try {
      // Декодируем токен для получения информации
      const decoded = jwtDecode(credentialResponse.credential);
      console.log('Decoded Google token:', decoded);
      
      // Создаем пользователя на основе Google данных
      const googleUser = {
        _id: `google_${decoded.sub}`,
        username: decoded.name || decoded.email.split('@')[0],
        email: decoded.email,
        name: decoded.name,
        picture: decoded.picture,
        role: 'visitor',
        token: credentialResponse.credential,
        googleId: decoded.sub,
        createdAt: new Date().toISOString()
      };
      
      // Сохраняем в localStorage
      localStorage.setItem('museumUser', JSON.stringify(googleUser));
      
      // Обновляем состояние авторизации
      loginWithGoogle(googleUser);
      
      alert(`Вход выполнен как ${decoded.name}`);
      window.location.href = '/'; // Переходим на главную
      
    } catch (error) {
      console.error('Google OAuth Error:', error);
      alert('Ошибка обработки Google токена');
    }
  };

  const handleError = () => {
    console.error('Google Login Failed');
    alert('Не удалось войти через Google. Проверьте настройки OAuth.');
  };

  return (
    <div className="google-auth-wrapper">
      <div className="google-divider">
        <span>Или войдите через Google</span>
      </div>
      
      <GoogleLogin
        onSuccess={handleSuccess}
        onError={handleError}
        size="large"
        theme="filled_blue"
        text="signin_with"
        shape="rectangular"
        locale="ru"
        width="300"
      />
      
      <div className="google-note">
        <p>Для работы требуется Client ID в .env файле</p>
      </div>
    </div>
  );
};

export default GoogleAuthButton;
