import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../components/AuthContext';
import '../App.css';

const ExhibitForm = ({ mode = 'create' }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user, addNotification } = useAuth();
  
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: '–ê—Ä—Ö–µ–æ–ª–æ–≥–∏—è',
    location: {
      hall: '',
      room: '',
      floor: ''
    },
    status: '–í —ç–∫—Å–ø–æ–∑–∏—Ü–∏–∏',
    conservationState: '–•–æ—Ä–æ—à–µ–µ',
    imageUrl: '',
    year: new Date().getFullYear(),
    materials: [],
    dimensions: {
      height: '',
      width: '',
      depth: ''
    },
    value: '–°—Ä–µ–¥–Ω—è—è'
  });

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ —ç–∫—Å–ø–æ–Ω–∞—Ç–∞ –¥–ª—è —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
  useEffect(() => {
    if (mode === 'edit' && id) {
      loadExhibit();
    }
  }, [mode, id]);

  const loadExhibit = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:5000/api/bits/${id}`, {
        headers: {
          'Authorization': `Bearer ${user?.token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setFormData({
            ...data.exhibit,
            location: data.exhibit.location || { hall: '', room: '', floor: '' },
            dimensions: data.exhibit.dimensions || { height: '', width: '', depth: '' },
            materials: data.exhibit.materials || []
          });
        }
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏:', error);
      addNotification('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –¥–∞–Ω–Ω—ã–µ —ç–∫—Å–ø–æ–Ω–∞—Ç–∞', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleMaterialsChange = (e) => {
    const materials = e.target.value.split(',').map(m => m.trim()).filter(m => m);
    setFormData(prev => ({
      ...prev,
      materials
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –ø—Ä–∞–≤–∞ (—Ç–æ–ª—å–∫–æ –∞–¥–º–∏–Ω –º–æ–∂–µ—Ç —Å–æ—Ö—Ä–∞–Ω—è—Ç—å)
    if (!user || user.role !== 'admin') {
      addNotification('–¢–æ–ª—å–∫–æ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä—ã –º–æ–≥—É—Ç —Å–æ—Ö—Ä–∞–Ω—è—Ç—å —ç–∫—Å–ø–æ–Ω–∞—Ç—ã', 'error');
      return;
    }
    
    setLoading(true);
    
    try {
      const url = mode === 'edit' 
        ? `http://localhost:5000/api/bits/${id}`
        : 'http://localhost:5000/api/bits';
      
      const method = mode === 'edit' ? 'PUT' : 'POST';
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify(formData)
      });
      
      const data = await response.json();
      
      if (data.success) {
        addNotification(
          mode === 'edit' ? '–≠–∫—Å–ø–æ–Ω–∞—Ç —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω' : '–≠–∫—Å–ø–æ–Ω–∞—Ç —É—Å–ø–µ—à–Ω–æ —Å–æ–∑–¥–∞–Ω',
          'success'
        );
        navigate(`/exhibits/${mode === 'edit' ? id : data.exhibit?._id}`);
      } else {
        addNotification(data.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏', 'error');
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è:', error);
      addNotification('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ —ç–∫—Å–ø–æ–Ω–∞—Ç–∞', 'error');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>{mode === 'edit' ? '–ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö...' : '–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ...'}</p>
      </div>
    );
  }

  return (
    <div className="exhibit-form-container">
      <div className="form-header">
        <h1>{mode === 'edit' ? '‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —ç–∫—Å–ø–æ–Ω–∞—Ç–∞' : 'üè∫ –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –Ω–æ–≤–æ–≥–æ —ç–∫—Å–ø–æ–Ω–∞—Ç–∞'}</h1>
        <p>{mode === 'edit' ? '–ò–∑–º–µ–Ω–∏—Ç–µ –¥–∞–Ω–Ω—ã–µ —ç–∫—Å–ø–æ–Ω–∞—Ç–∞' : '–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –Ω–æ–≤–æ–º —ç–∫—Å–ø–æ–Ω–∞—Ç–µ'}</p>
        
        {!user || user.role !== 'admin' ? (
          <div className="admin-warning">
            ‚ö†Ô∏è –¢–æ–ª—å–∫–æ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä—ã –º–æ–≥—É—Ç {mode === 'edit' ? '—Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å' : '–¥–æ–±–∞–≤–ª—è—Ç—å'} —ç–∫—Å–ø–æ–Ω–∞—Ç—ã
          </div>
        ) : null}
      </div>

      <form onSubmit={handleSubmit} className="exhibit-form">
        {/* –û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è */}
        <div className="form-section">
          <h2>üìã –û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è</h2>
          
          <div className="form-group">
            <label htmlFor="name">–ù–∞–∑–≤–∞–Ω–∏–µ *</label>
            <input
              id="name"
              name="name"
              type="text"
              value={formData.name}
              onChange={handleChange}
              required
              disabled={!user || user.role !== 'admin'}
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="description">–û–ø–∏—Å–∞–Ω–∏–µ</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="4"
              disabled={!user || user.role !== 'admin'}
            />
          </div>
          
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="category">–ö–∞—Ç–µ–≥–æ—Ä–∏—è</label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                disabled={!user || user.role !== 'admin'}
              >
