import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../components/AuthContext';
import '../App.css';

const ExhibitForm = ({ mode = 'create' }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user, addNotification } = useAuth();
  
  const [loading, setLoading] = useState(false);
  const [employees, setEmployees] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: 'ĞÑ€Ñ…ĞµĞ¾Ğ»Ğ¾Ğ³Ğ¸Ñ',
    location: {
      hall: '',
      room: '',
      floor: ''
    },
    status: 'Ğ’ ÑĞºÑĞ¿Ğ¾Ğ·Ğ¸Ñ†Ğ¸Ğ¸',
    conservationState: 'Ğ¥Ğ¾Ñ€Ğ¾ÑˆĞµĞµ',
    assignedEmployee: '', 
    imageUrl: '',
    year: new Date().getFullYear(),
    materials: [],
    dimensions: {
      height: '',
      width: '',
      depth: ''
    },
    value: 'Ğ¡Ñ€ĞµĞ´Ğ½ÑÑ'
  });

  useEffect(() => {
    loadEmployees();
    
    if (mode === 'edit' && id) {
      loadExhibit();
    }
  }, [mode, id]);

  const loadEmployees = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/employees', {
        headers: {
          'Authorization': `Bearer ${user?.token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setEmployees(data.employees || []);
        }
      }
    } catch (error) {
      console.error('ĞÑˆĞ¸Ğ±ĞºĞ° Ğ·Ğ°Ğ³Ñ€ÑƒĞ·ĞºĞ¸ ÑĞ¾Ñ‚Ñ€ÑƒĞ´Ğ½Ğ¸ĞºĞ¾Ğ²:', error);
      
      setEmployees([
        { _id: '1', name: 'Ğ˜Ğ²Ğ°Ğ½Ğ¾Ğ²Ğ° Ğ.Ğ¡.', position: 'ĞºÑƒÑ€Ğ°Ñ‚Ğ¾Ñ€', department: 'ĞÑ‚Ğ´ĞµĞ» Ğ¶Ğ¸Ğ²Ğ¾Ğ¿Ğ¸ÑĞ¸' },
        { _id: '2', name: 'ĞŸĞµÑ‚Ñ€Ğ¾Ğ² Ğ”.Ğ’.', position: 'Ñ€ĞµÑÑ‚Ğ°Ğ²Ñ€Ğ°Ñ‚Ğ¾Ñ€', department: 'Ğ ĞµÑÑ‚Ğ°Ğ²Ñ€Ğ°Ñ†Ğ¸Ğ¾Ğ½Ğ½Ñ‹Ğ¹ Ğ¾Ñ‚Ğ´ĞµĞ»' },
        { _id: '3', name: 'Ğ¡Ğ¸Ğ´Ğ¾Ñ€Ğ¾Ğ²Ğ° Ğ•.Ğœ.', position: 'ÑĞºÑĞºÑƒÑ€ÑĞ¾Ğ²Ğ¾Ğ´', department: 'Ğ­ĞºÑĞºÑƒÑ€ÑĞ¸Ğ¾Ğ½Ğ½Ñ‹Ğ¹ Ğ¾Ñ‚Ğ´ĞµĞ»' },
        { _id: '4', name: 'ĞšÑƒĞ·Ğ½ĞµÑ†Ğ¾Ğ² Ğ.ĞŸ.', position: 'ÑĞ¼Ğ¾Ñ‚Ñ€Ğ¸Ñ‚ĞµĞ»ÑŒ', department: 'Ğ¡Ğ»ÑƒĞ¶Ğ±Ğ° Ğ±ĞµĞ·Ğ¾Ğ¿Ğ°ÑĞ½Ğ¾ÑÑ‚Ğ¸' },
        { _id: '5', name: 'ĞœĞ¾Ñ€Ğ¾Ğ·Ğ¾Ğ²Ğ° Ğ.Ğ’.', position: 'Ğ°Ğ´Ğ¼Ğ¸Ğ½Ğ¸ÑÑ‚Ñ€Ğ°Ñ‚Ğ¾Ñ€', department: 'ĞĞ´Ğ¼Ğ¸Ğ½Ğ¸ÑÑ‚Ñ€Ğ°Ñ†Ğ¸Ñ' }
      ]);
    }
  };

  const loadExhibit = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:5000/api/bits/${id}/with-employee`, {
        headers: {
          'Authorization': `Bearer ${user?.token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        if (data.success && data.exhibit) {
         
          setFormData({
            name: data.exhibit.name || '',
            description: data.exhibit.description || '',
            category: data.exhibit.category || 'ĞÑ€Ñ…ĞµĞ¾Ğ»Ğ¾Ğ³Ğ¸Ñ',
            location: data.exhibit.location || { hall: '', room: '', floor: '' },
            status: data.exhibit.status || 'Ğ’ ÑĞºÑĞ¿Ğ¾Ğ·Ğ¸Ñ†Ğ¸Ğ¸',
            conservationState: data.exhibit.conservationState || 'Ğ¥Ğ¾Ñ€Ğ¾ÑˆĞµĞµ',
            assignedEmployee: data.exhibit.assignedEmployee?.id || '',
            imageUrl: data.exhibit.imageUrl || '',
            year: data.exhibit.year || new Date().getFullYear(),
            materials: Array.isArray(data.exhibit.materials) ? data.exhibit.materials : [],
            dimensions: data.exhibit.dimensions || { height: '', width: '', depth: '' },
            value: data.exhibit.value || 'Ğ¡Ñ€ĞµĞ´Ğ½ÑÑ'
          });
        }
      } else {
        throw new Error('ĞĞµ ÑƒĞ´Ğ°Ğ»Ğ¾ÑÑŒ Ğ·Ğ°Ğ³Ñ€ÑƒĞ·Ğ¸Ñ‚ÑŒ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ ÑĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚Ğ°');
      }
    } catch (error) {
      console.error('ĞÑˆĞ¸Ğ±ĞºĞ° Ğ·Ğ°Ğ³Ñ€ÑƒĞ·ĞºĞ¸:', error);
      addNotification('ĞĞµ ÑƒĞ´Ğ°Ğ»Ğ¾ÑÑŒ Ğ·Ğ°Ğ³Ñ€ÑƒĞ·Ğ¸Ñ‚ÑŒ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ ÑĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚Ğ°', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleMaterialsChange = (e) => {
    const materials = e.target.value.split(',').map(m => m.trim()).filter(m => m);
    setFormData(prev => ({
      ...prev,
      materials
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!user || user.role !== 'admin') {
      addNotification('Ğ¢Ğ¾Ğ»ÑŒĞºĞ¾ Ğ°Ğ´Ğ¼Ğ¸Ğ½Ğ¸ÑÑ‚Ñ€Ğ°Ñ‚Ğ¾Ñ€Ñ‹ Ğ¼Ğ¾Ğ³ÑƒÑ‚ ÑĞ¾Ñ…Ñ€Ğ°Ğ½ÑÑ‚ÑŒ ÑĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚Ñ‹', 'error');
      return;
    }
    
    if (!formData.assignedEmployee) {
      addNotification('ĞŸĞ¾Ğ¶Ğ°Ğ»ÑƒĞ¹ÑÑ‚Ğ°, Ğ²Ñ‹Ğ±ĞµÑ€Ğ¸Ñ‚Ğµ Ğ¾Ñ‚Ğ²ĞµÑ‚ÑÑ‚Ğ²ĞµĞ½Ğ½Ğ¾Ğ³Ğ¾ ÑĞ¾Ñ‚Ñ€ÑƒĞ´Ğ½Ğ¸ĞºĞ°', 'error');
      return;
    }
    
    setLoading(true);
    
    try {
      const url = mode === 'edit' 
        ? `http://localhost:5000/api/bits/${id}`
        : 'http://localhost:5000/api/bits';
      
      const method = mode === 'edit' ? 'PUT' : 'POST';
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify(formData)
      });
      
      const data = await response.json();
      
      if (data.success) {
        addNotification(
          mode === 'edit' ? 'Ğ­ĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚ ÑƒÑĞ¿ĞµÑˆĞ½Ğ¾ Ğ¾Ğ±Ğ½Ğ¾Ğ²Ğ»ĞµĞ½' : 'Ğ­ĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚ ÑƒÑĞ¿ĞµÑˆĞ½Ğ¾ ÑĞ¾Ğ·Ğ´Ğ°Ğ½',
          'success'
        );
        navigate(`/exhibits/${mode === 'edit' ? id : data.bit?._id}`);
      } else {
        addNotification(data.message || 'ĞÑˆĞ¸Ğ±ĞºĞ° Ğ¿Ñ€Ğ¸ ÑĞ¾Ñ…Ñ€Ğ°Ğ½ĞµĞ½Ğ¸Ğ¸', 'error');
      }
    } catch (error) {
      console.error('ĞÑˆĞ¸Ğ±ĞºĞ° ÑĞ¾Ñ…Ñ€Ğ°Ğ½ĞµĞ½Ğ¸Ñ:', error);
      addNotification('ĞÑˆĞ¸Ğ±ĞºĞ° Ğ¿Ñ€Ğ¸ ÑĞ¾Ñ…Ñ€Ğ°Ğ½ĞµĞ½Ğ¸Ğ¸ ÑĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚Ğ°', 'error');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>{mode === 'edit' ? 'Ğ—Ğ°Ğ³Ñ€ÑƒĞ·ĞºĞ° Ğ´Ğ°Ğ½Ğ½Ñ‹Ñ…...' : 'Ğ¡Ğ¾Ñ…Ñ€Ğ°Ğ½ĞµĞ½Ğ¸Ğµ...'}</p>
      </div>
    );
  }

  return (
    <div className="exhibit-form-container">
