        <h1>{mode === 'edit' ? 'âœï¸ Ğ ĞµĞ´Ğ°ĞºÑ‚Ğ¸Ñ€Ğ¾Ğ²Ğ°Ğ½Ğ¸Ğµ ÑĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚Ğ°' : 'ğŸº Ğ¡Ğ¾Ğ·Ğ´Ğ°Ğ½Ğ¸Ğµ Ğ½Ğ¾Ğ²Ğ¾Ğ³Ğ¾ ÑĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚Ğ°'}</h1>
        <p>{mode === 'edit' ? 'Ğ˜Ğ·Ğ¼ĞµĞ½Ğ¸Ñ‚Ğµ Ğ´Ğ°Ğ½Ğ½Ñ‹Ğµ ÑĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚Ğ°' : 'Ğ—Ğ°Ğ¿Ğ¾Ğ»Ğ½Ğ¸Ñ‚Ğµ Ğ¸Ğ½Ñ„Ğ¾Ñ€Ğ¼Ğ°Ñ†Ğ¸Ñ Ğ¾ Ğ½Ğ¾Ğ²Ğ¾Ğ¼ ÑĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚Ğµ'}</p>
        
        {!user || user.role !== 'admin' ? (
          <div className="admin-warning">
            âš ï¸ Ğ¢Ğ¾Ğ»ÑŒĞºĞ¾ Ğ°Ğ´Ğ¼Ğ¸Ğ½Ğ¸ÑÑ‚Ñ€Ğ°Ñ‚Ğ¾Ñ€Ñ‹ Ğ¼Ğ¾Ğ³ÑƒÑ‚ {mode === 'edit' ? 'Ñ€ĞµĞ´Ğ°ĞºÑ‚Ğ¸Ñ€Ğ¾Ğ²Ğ°Ñ‚ÑŒ' : 'Ğ´Ğ¾Ğ±Ğ°Ğ²Ğ»ÑÑ‚ÑŒ'} ÑĞºÑĞ¿Ğ¾Ğ½Ğ°Ñ‚Ñ‹
          </div>
        ) : null}
      </div>

      <form onSubmit={handleSubmit} className="exhibit-form">
      
        <div className="form-section">
          <h2>ğŸ“‹ ĞÑĞ½Ğ¾Ğ²Ğ½Ğ°Ñ Ğ¸Ğ½Ñ„Ğ¾Ñ€Ğ¼Ğ°Ñ†Ğ¸Ñ</h2>
          
          <div className="form-group">
            <label htmlFor="name">ĞĞ°Ğ·Ğ²Ğ°Ğ½Ğ¸Ğµ *</label>
