        </div>
      </div>

      <div className="exhibits-list">
        <h3>–°–ø–∏—Å–æ–∫ —ç–∫—Å–ø–æ–Ω–∞—Ç–æ–≤ ({filteredExhibits.length})</h3>
        
        {filteredExhibits.map(exhibit => (
          <div key={exhibit.id} className="conservation-item">
            <div className="item-main">
              <div className="item-header">
                <h4>{exhibit.name}</h4>
                <span 
                  className="conservation-badge"
                  style={{ backgroundColor: getConservationColor(exhibit.conservationState) }}
                >
                  {getConservationText(exhibit.conservationState)}
                </span>
              </div>
              
              <div className="item-details">
                <div className="detail-group">
                  <span className="detail-label">–¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞:</span>
                  <span className="detail-value">{exhibit.temperature}¬∞C</span>
                </div>
                <div className="detail-group">
                  <span className="detail-label">–í–ª–∞–∂–Ω–æ—Å—Ç—å:</span>
                  <span className="detail-value">{exhibit.humidity}%</span>
                </div>
                <div className="detail-group">
                  <span className="detail-label">–ü–æ—Å–ª–µ–¥–Ω—è—è –ø—Ä–æ–≤–µ—Ä–∫–∞:</span>
                  <span className="detail-value">{exhibit.lastCheck}</span>
