      duration: 90,
      maxVisitors: 15,
      price: 30,
      schedule: ["13:00", "15:00"]
    },
    {
      id: 3,
      name: "–®–µ–¥–µ–≤—Ä—ã —ç–ø–æ—Ö–∏ –í–æ–∑—Ä–æ–∂–¥–µ–Ω–∏—è",
      description: "–ò—Å–∫—É—Å—Å—Ç–≤–æ –∏ –∫—É–ª—å—Ç—É—Ä–∞ –ï–≤—Ä–æ–ø—ã XIV-XVII –≤–µ–∫–æ–≤",
      duration: 150,
      maxVisitors: 20,
      price: 16,
      schedule: ["12:00", "16:00"]
    }
  ];

  const handleBookTour = () => {
    if (!selectedTour) {
      alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ —ç–∫—Å–∫—É—Ä—Å–∏—é');
      return;
