    const featured = exhibits
      .filter(exhibit => exhibit.status === '–í —ç–∫—Å–ø–æ–∑–∏—Ü–∏–∏')
      .slice(0, 6);
    
    // –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞
    const newStats = {
      total: exhibits.length,
          };
    
